'use strict';
const DB = require('.');
const c1 = DB('foo', {memory: true, fileMustExist: true});
const c2 = DB('foo', {memory: true});
c1.prepare('create table data (a,b,c)').run();

console.log('c1 inserting...');
c1.prepare('INSERT INTO data VALUES (12, 34, 56)').run();
console.log('c1 updating...');
c1.prepare('UPDATE data SET a = 78').run();

console.log('c2 updating...');
c2.prepare('UPDATE data SET a = 78').run();
